import React, { useEffect, useState } from 'react'
import { Card, Button, Table, Space, Modal, Form, Input } from 'antd'
import { noHomeWorkList } from '../../../axios/api'
const { confirm } = Modal;


const Manager = () => {
    const [noHomeWorkList, setNoHomeWorkList] = useState([])

    useEffect(() => {
        getNoHomeWorkList()
    }, [])

    const getNoHomeWorkList = async () => {

    }

    const dataSource = [
        {
            key: '1',
            name: '汪红光',
            sid: 2702170119,
            creatTime: '2020-8-15 19:00',
        }
    ];

    const columns = [
        {
            title: '姓名',
            dataIndex: 'name',
            key: 'name',
        },
        {
            title: '学号',
            dataIndex: 'sid',
            key: 'sid',
            sorter: () => { }
        },
        {
            title: '提交时间',
            dataIndex: 'creatTime',
            key: 'creatTime',
            sorter: () => { }
        },
        {
            title: '操作',
            dataIndex: 'action',
            key: 'action',
            render: (text, record) => {
                return <Space size="middle">
                    <a onClick={() => deleteData(record)}>删除</a>
                    <a>导出</a>
                </Space>
            }
        }
    ];

    const layout = {
        labelCol: {
            span: 6,
        },
        wrapperCol: {
            span: 16,
        },
    }
    const tailLayout = {
        wrapperCol: {
            offset: 10,
            span: 14,
        },
    }

    const deleteData = (record) => {
        confirm({
            title: '确定删除这条数据?',
            onOk() {
                console.log('OK');
            },
            onCancel() {
                console.log('Cancel');
            },
        });
        console.log(record)
    }

    const deleteAll = () => {
        confirm({
            title: '确定清空数据?',
            onOk() {
                console.log('OK');
            },
            onCancel() {
                console.log('Cancel');
            },
        });
    }
    const onFinish = (value) => {
        console.log(value)
    }
    return (
        <React.Fragment>
            <h3>管理作业</h3>
            <Card title='作业未上交名单' style={{ marginBottom: '50px' }} >
                <div style={{ display: 'flex', flexWrap: 'wrap' }}>
                    <div style={{ width: '50px', textAlign: 'center', color: 'red' }}>汪红光</div>
                    <div style={{ width: '50px', textAlign: 'center' }}>汪红光</div>
                    <div style={{ width: '50px', textAlign: 'center' }}>汪红光</div>
                    <div style={{ width: '50px', textAlign: 'center' }}>汪红光</div>
                    <div style={{ width: '50px', textAlign: 'center' }}>汪红光</div>
                    <div style={{ width: '50px', textAlign: 'center' }}>汪红光</div>
                    <div style={{ width: '50px', textAlign: 'center' }}>汪红光</div>
                </div>
            </Card>
            <Card title='作业内容' style={{ marginBottom: '50px' }}>
                <div style={{ width: '600px', margin: 'auto' }}>
                    <Form
                        {...layout}
                        name="basic"
                        onFinish={onFinish}
                    >
                        <Form.Item
                            label="上传作业内容"
                            name="content"
                            rules={[{ required: true, message: '请输入作业内容' }]}
                        >
                            <Input />
                        </Form.Item>

                        <Form.Item
                            label="命名格式"
                            name="format"
                            rules={[{ required: true, message: '请输入命名格式' }]}
                        >
                            <Input />
                        </Form.Item>
                        <Form.Item {...tailLayout}>
                            <Button type="primary" htmlType="submit">
                                提交
                        </Button>
                        </Form.Item>
                    </Form>
                </div>
            </Card>
            <Card title='作业列表' extra={
                <div>
                    <Button type='primary' style={{ fontSize: "14px" }}>全部导出</Button>
                    <Button type='primary' style={{ fontSize: "14px", marginLeft: '10px' }} onClick={deleteAll}>清空数据</Button>
                </div>}>
                <Table dataSource={dataSource} columns={columns} />
            </Card>
        </React.Fragment>
    )
}

export default Manager
