import React, { useState, useEffect } from 'react'
import { Card, Button, Upload, message } from 'antd'
import { UploadOutlined } from '@ant-design/icons';
import { getFileName } from '../../../axios/api'

const UploadWork = () => {
    const [fileList, setFileList] = useState([])
    const [uploading, setUploading] = useState(false)
    const [fileName, setFileName] = useState({})



    //初始化数据
    useEffect(() => {
        toGetFileName()
    }, [])

    //获取文件名以及格式
    const toGetFileName = async () => {
        try {
            let { status, data, msg } = await getFileName()
            if (status == '200') {
                setFileName(data)
            } else {
                message.error(msg)
            }
        } catch (e) {
            message.error('请求文件名称接口失败')
        }
    }

    const props = {
        onRemove: file => {
            const index = fileList.indexOf(file);
            const newFileList = fileList.slice();
            newFileList.splice(index, 1)
            setFileList(newFileList)
        },
        beforeUpload: file => {
            setFileList([...fileList, file])
            return false;
        },
        fileList,
    };

    const handleUpload = () => {
        setUploading(true)
        setTimeout(() => {
            setUploading(false)
        }, 3000)
    }
    return <React.Fragment>
        <h3>上传作业</h3>
        <Card title='上传要求' style={{ width: '100%', marginBottom: '50px' }}>
            <div style={{ textAlign: 'center' }}>
                <div>
                    <span style={{ fontSize: '14px', fontWeight: 'bold' }}>本次上传的作业：</span>
                    <span style={{ color: 'red' }}>徐精明老师的安卓作业</span>
                </div>
                <div>
                    <span style={{ fontSize: '14px', fontWeight: 'bold' }}>命名方式：</span>
                    <span style={{ color: 'red' }}>2702170119</span>
                </div>
            </div>
        </Card>
        <Card title='上传内容'>
            <Upload {...props}>
                <Button>
                    <UploadOutlined />  上传
                </Button>
            </Upload>
            <Button
                type="primary"
                onClick={handleUpload}
                disabled={fileList.length === 0}
                loading={uploading}
                style={{ marginTop: 16 }}
            >
                {uploading ? '上传中' : '开始上传'}
            </Button>
        </Card>
    </React.Fragment>
}

export default UploadWork
