import React, { useState } from 'react'
import { Layout, Card, Form, Input, Button, Menu } from 'antd';
import ChangePassword from './changePassword'
import UploadWork from './uploadWork'
import OutLogin from './outLogin'
const { Header, Content, Sider } = Layout;

const Upload = () => {
    const [show, setShow] = useState('changep')

    const getRight = () => {
        if (show === 'changep') {
            return <ChangePassword />
        } else if (show === 'uploadwork') {
            return <UploadWork />
        }
    }
    return (
        <React.Fragment>
            <Layout style={{ height: '100%' }}>
                <Header style={{ display: 'flex', justifyContent: 'space-between' }}>
                    <h1 style={{ color: '#fff' }}>作业上传</h1>
                    <OutLogin />
                </Header>
                <Layout>
                    <Sider style={{ overflow: 'hidden', background: 'white' }}>
                        <Menu style={{ borderRight: 0 ,fontSize:'14px'}}
                            defaultSelectedKeys={['changep']}
                            onClick={({ item, key, keyPath, domEvent }) => {
                                setShow(key)
                            }}

                        >
                            <Menu.Item key="changep" >
                                修改密码
                            </Menu.Item>
                            <Menu.Item key="uploadwork" >
                                上传作业文件
                            </Menu.Item>
                        </Menu>
                    </Sider>
                    <Content>
                        <div style={{padding:30}}>
                            {getRight()}
                        </div>
                    </Content>
                </Layout>
            </Layout>
        </React.Fragment>
    )
}

export default Upload
