import React, { useState } from 'react'
import { Layout, Card, Form, Input, Button, Menu } from 'antd';
import ChangePassword from './changePassword'
import UploadWork from './uploadWork'
import OutLogin from './outLogin'
const { Header, Content, Sider } = Layout;

const Upload = () => {
    const [show, setShow] = useState('changep')

    const getRight = () => {
        if (show === 'changep') {
            return <ChangePassword />
        } else if (show === 'uploadwork') {
            return <UploadWork />
        }
    }
    return (
        <React.Fragment>
            <Layout style={{ height: '100%' }}>
                <Header style={{display:'flex'}}>
                    <OutLogin />
                </Header>
                <Layout>
                    <Sider style={{ overflow: 'hidden' }}>
                        <Menu style={{ background: '#041527', color: 'white' }}
                            defaultSelectedKeys={['changep']}
                            onClick={({ item, key, keyPath, domEvent }) => {
                                setShow(key)
                            }}
                        >
                            <Menu.Item key="changep" >
                                修改密码
                            </Menu.Item>
                            <Menu.Item key="uploadwork" >
                                上传作业文件
                            </Menu.Item>
                        </Menu>
                    </Sider>
                    <Content>
                        {getRight()}
                    </Content>
                </Layout>
            </Layout>
        </React.Fragment>
    )
}

export default Upload
