import React, { useEffect, useState } from 'react'
import { Card, Button, Table, Space, Modal, Form, Input, message } from 'antd'
import { noHomeWorkListName, changeFileFormat, detailList } from '../../../axios/api'
const { confirm } = Modal;


const Manager = () => {
    const [noHomeWorkList, setNoHomeWorkList] = useState([])
    const [dataSource, setDataSource] = useState([])

    useEffect(() => {
        getNoHomeWorkList()
    }, [])

    const onFinish = (value) => {
        toChangeFileFormat(value)
    }

    //修改文件名称的接口
    const toChangeFileFormat = async (param) => {
        try {
            let { msg, status } = await changeFileFormat(param)
            if (status == '200') {
                message.success('修改成功')
            } else {
                message.error(msg)
            }
        } catch (e) {
            console.log(e)
            message.error("访问修改接口失败")
        }
    }


    //获取未完成作业的列表
    const getNoHomeWorkList = async () => {
        try {
            let { data, status, msg } = await noHomeWorkListName()
            if (status == '200') {
                message.success('获取未完成作业列表成功')
                setNoHomeWorkList(data.data)
            } else {
                message.error(msg)
            }
        } catch (e) {
            console.log(e)
            message.error('获取未完成作业接口失败')
        }
    }



    const columns = [
        {
            title: '姓名',
            dataIndex: 'name',
            key: 'name',
        },
        {
            title: '学号',
            dataIndex: 'sid',
            key: 'sid',
            sorter: () => { }
        }, {
            title: '文件名称',
            dataIndex: 'fileName',
            key: 'fileName'
        },
        {
            title: '提交时间',
            dataIndex: 'ctime',
            key: 'ctime',
            sorter: () => { }
        },
        {
            title: '操作',
            dataIndex: 'action',
            key: 'action',
            render: (text, record) => {
                return <Space size="middle">
                    <a onClick={() => deleteData(record)}>删除</a>
                    <a>导出</a>
                </Space>
            }
        }
    ];

    const layout = {
        labelCol: {
            span: 6,
        },
        wrapperCol: {
            span: 16,
        },
    }
    const tailLayout = {
        wrapperCol: {
            offset: 10,
            span: 14,
        },
    }

    const deleteData = (record) => {
        confirm({
            title: '确定删除这条数据?',
            onOk() {
                console.log('OK');
            },
            onCancel() {
                console.log('Cancel');
            },
        });
        console.log(record)
    }

    const deleteAll = () => {
        confirm({
            title: '确定清空数据?',
            onOk() {
                console.log('OK');
            },
            onCancel() {
                console.log('Cancel');
            },
        });
    }

    return (
        <React.Fragment>
            <h3>管理作业</h3>
            <Card title='作业未上交名单' style={{ marginBottom: '50px' }} >
                <div style={{ display: 'flex', flexWrap: 'wrap' }}>
                    {
                        Array.isArray(noHomeWorkList) ? noHomeWorkList.map(item => {
                            return <div key={item} style={{ width: '50px', textAlign: 'center', color: 'red' }}>{item}</div>
                        }) : null
                    }
                </div>
            </Card>
            <Card title='作业内容' style={{ marginBottom: '50px' }}>
                <div style={{ width: '600px', margin: 'auto' }}>
                    <Form
                        {...layout}
                        name="basic"
                        onFinish={onFinish}
                    >
                        <Form.Item
                            label="上传作业内容"
                            name="content"
                            rules={[{ required: true, message: '请输入作业内容' }]}
                        >
                            <Input />
                        </Form.Item>

                        <Form.Item
                            label="命名格式"
                            name="format"
                            rules={[{ required: true, message: '请输入命名格式' }]}
                        >
                            <Input />
                        </Form.Item>
                        <Form.Item {...tailLayout}>
                            <Button type="primary" htmlType="submit">
                                提交
                        </Button>
                        </Form.Item>
                    </Form>
                </div>
            </Card>
            <Card title='作业列表' extra={
                <div>
                    <Button type='primary' style={{ fontSize: "14px" }}>全部导出</Button>
                    <Button type='primary' style={{ fontSize: "14px", marginLeft: '10px' }} onClick={deleteAll}>清空数据</Button>
                </div>}>
                <Table dataSource={dataSource} columns={columns} />
            </Card>
        </React.Fragment>
    )
}

export default Manager
