import React from 'react'
import { Card, Button, Table, Space, Modal } from 'antd'
const { confirm } = Modal;


const Manager = () => {
    const dataSource = [
        {
            key: '1',
            name: '汪红光',
            sid: 2702170119,
            creatTime: '2020-8-15 19:00',
        }
    ];

    const columns = [
        {
            title: '姓名',
            dataIndex: 'name',
            key: 'name',
        },
        {
            title: '学号',
            dataIndex: 'sid',
            key: 'sid',
        },
        {
            title: '提交时间',
            dataIndex: 'creatTime',
            key: 'creatTime',
        },
        {
            title: '操作',
            dataIndex: 'action',
            key: 'action',
            render: (text, record) => {
                return <Space size="middle">
                    <a onClick={() => deleteData(record)}>删除</a>
                    <a>导出</a>
                </Space>
            }
        }
    ];

    const deleteData = (record) => {
        confirm({
            title: '确定删除这条数据?',
            onOk() {
                console.log('OK');
            },
            onCancel() {
                console.log('Cancel');
            },
        });
        console.log(record)
    }

    const deleteAll = () =>{
        confirm({
            title: '确定清空数据?',
            onOk() {
                console.log('OK');
            },
            onCancel() {
                console.log('Cancel');
            },
        });
    }
    return (
        <React.Fragment>
            <h3>管理作业</h3>
            <Card title='作业未上交名单' style={{ marginBottom: '50px' }} >
                <div style={{ display: 'flex', flexWrap: 'wrap' }}>
                    <div style={{ width: '50px', textAlign: 'center', color: 'red' }}>汪红光</div>
                    <div style={{ width: '50px', textAlign: 'center' }}>汪红光</div>
                    <div style={{ width: '50px', textAlign: 'center' }}>汪红光</div>
                    <div style={{ width: '50px', textAlign: 'center' }}>汪红光</div>
                    <div style={{ width: '50px', textAlign: 'center' }}>汪红光</div>
                    <div style={{ width: '50px', textAlign: 'center' }}>汪红光</div>
                    <div style={{ width: '50px', textAlign: 'center' }}>汪红光</div>
                </div>
            </Card>
            <Card title='作业列表'>
                <Table dataSource={dataSource} columns={columns} />
            </Card>
            <Card title='作业列表' extra={
                <div>
                    <Button type='primary' style={{ fontSize: "14px" }}>全部导出</Button>
                    <Button type='primary' style={{ fontSize: "14px", marginLeft: '10px' }} onClick={deleteAll}>清空数据</Button>
                </div>}>
                <Table dataSource={dataSource} columns={columns} />
            </Card>
        </React.Fragment>
    )
}

export default Manager
