import React from 'react'
import { Layout, Card, Form, Input, Button } from 'antd';
const { Header, Footer, Content } = Layout;

const Login = () => {
    const layout = {
        labelCol: {
            span: 6,
        },
        wrapperCol: {
            span: 16,
        },
    }
    const tailLayout = {
        wrapperCol: {
            offset: 8,
            span: 16,
        },
    }

    const onFinish = (value) => {
        console.log(value)
    }

    const onFinishFailed = (errInfo) => {
        console.log(errInfo)
    }
    return (
        <React.Fragment>
            <Layout style={{ height: '100%' }}>
                <Header style={{ textAlign: 'center', color: 'white' }}>
                    登陆界面
                </Header>
                <Content style={{ margin: 'auto', marginTop: '100px' }}>
                    <Card style={{ width: 500 }}>
                        <Form
                            {...layout}
                            name="basic"
                            onFinish={onFinish}
                            onFinishFailed={onFinishFailed}
                        >
                            <Form.Item
                                label="学号"
                                name="username"
                                rules={[
                                    {
                                        required: true,
                                        message: '请输入你的姓名',
                                    },
                                ]}
                            >
                                <Input />
                            </Form.Item>

                            <Form.Item
                                label="密码"
                                name="password"
                                rules={[
                                    {
                                        required: true,
                                        message: '请输入你的密码',
                                    },
                                ]}
                            >
                                <Input.Password />
                            </Form.Item>
                            <Form.Item {...tailLayout}>
                                <Button type="primary" >
                                    登陆
                                </Button>
                            </Form.Item>
                        </Form>
                    </Card>
                </Content>
                <Footer style={{ background: '#041527', color: 'white', textAlign: 'center' }}>
                    网络工程171
                </Footer>
            </Layout>
        </React.Fragment>
    )
}

export default Login
